import React, { useEffect, useState, useCallback } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faGear } from '@fortawesome/free-solid-svg-icons';
import * as memberApi from "../../api/memberApi"

export default function MemberSummary({ memberNo }) {

    // State로 회원정보를 저장
    const [summary, setSummary] = useState({
        name: '',
        points: 0,
        inquiryCount: 0,
        counselCount: 0,
        followCount: 0
    })

    //회원 요약정보 목록 가져옴1
       // fetchCountSummary를 useCallback으로 메모이제이션
       const fetchCountSummary = useCallback(async () => {
        try {
            const response = await memberApi.viewMypageSummary(memberNo);
            const data = await response.json();

            setSummary((prevState) => ({
                ...prevState,
                inquiryCount: data.inquiryCount || 0,
                counselCount: data.counselCount || 0,
                followCount: data.followCount || 0,
            }));
        } catch (error) {
            console.error('멘티 요약정보 조회 실패:', error);
        }
    }, [memberNo]); // memberNo가 변경되면 함수 재생성

    useEffect(() => {
        fetchCountSummary();
    }, [fetchCountSummary]); // 의존성 배열에 fetchCountSummary 추가

    return (
    <section class="summary">
        <div class="summary-box">
            <h1>{summary.name} 님 안녕하세요 <a href="#"><FontAwesomeIcon icon={faGear} /> 개인정보 변경</a></h1>
            <p>적립금 <span>{summary.points}자루</span></p>
            <div class="summary-stats">
                <div>
                    <h2>{summary.inquiryCount}</h2>
                    <p>질문 수</p>
                </div>
                <div>
                    <h2>{summary.counselCount}</h2>
                    <p>상담 횟수</p>
                </div>
                <div>
                    <h2>{summary.followCount}</h2>
                    <p>팔로잉</p>
                </div>
            </div>
            <button class="mentor-btn">멘토전환</button>
        </div>
    </section>
  )
}
