import React from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faGear } from '@fortawesome/free-solid-svg-icons';
import * as memberApi from "../../api/memberApi"

export default function MemberSummary({MemberNo}) {
    // State로 회원정보를 저장
    const [summary, setSummary] = useState({
        name: '',
        points: 0,
        inquiryCount: 0,
        counselCount: 0,
        followCount: 0
    })

    //회원 요약정보 목록 가져옴
    const fetchSummary = () => {
        try {
            const response = memberApi.viewMypageSummary();
            setSummary(response.data);
        } catch (error) {

        }
    }

    return (
    <section class="summary">
        <div class="summary-box">
            <h1>홍길동 님 안녕하세요 <a href=""><FontAwesomeIcon icon={faGear} /> 개인정보 변경</a></h1>
            <p>적립금 <span>5,000자루</span></p>
            <div class="summary-stats">
                <div>
                    <h2>9</h2>
                    <p>질문 수</p>
                </div>
                <div>
                    <h2>11</h2>
                    <p>상담 횟수</p>
                </div>
                <div>
                    <h2>15</h2>
                    <p>팔로잉</p>
                </div>
            </div>
            <button class="mentor-btn">멘토전환</button>
        </div>
    </section>
  )
}
